package leetcode_test

import (
	"fmt"
	"testing"

	"github.com/stretchr/testify/require"
	"gitlab.com/gjerry134679/leetcode"
)

func TestMaxPalindromes(t *testing.T) {
	type testCase struct {
		s string
		k int
		a int
	}

	tcs := []testCase{
		{"abaccdbbd", 3, 2},
		{"adbcda", 2, 0},
		{"", 16, 30},
		{"", 1, 2000},
	}

	q := leetcode.Q2472{}
	for i := range tcs {
		tc := tcs[i]
		t.Run(
			fmt.Sprintf("Case %d-%s", i+1, tc.s),
			func(t *testing.T) {
				require.Equal(t, tc.a, q.MaxPalindromes(tc.s, tc.k))
			},
		)
	}
}
